import React, {useEffect, useState} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { postData, userData } from './action';
import { useNavigate, useParams } from 'react-router-dom';


export const Home = () => {
  const [search, setSearch] = useState("");
  const dispatch=useDispatch()
  const {post}= useSelector((state)=>state.post)
  const {user}= useSelector((state)=>state.user)
  const navigate=useNavigate();
  const params=useParams();
  useEffect(()=>{
    dispatch(postData())},[]
  )
  useEffect(()=>{
    dispatch(userData())},[]
  )
  return (<div style={{display:"grid", gridTemplateColumns:"1fr 1fr"}}>
    <div  >
      <p> The number of users is: {user.length}</p>
        {user
        .map((item)=>{return( <div key={post.id} style={{ border:"solid 1px black", margin:"10px"}}>
    <p> {item.name}</p>
    
    <button onClick={()=>navigate(`${item.id.toString()}`)}>post</button>
    </div>)})}</div>
   <div > 
   <input onChange={(e) => setSearch(e.target.value)}></input>
        {post
         .filter((item, index) => {
            return item.title.includes(search)||item.body.includes(search)||item.id.toString().includes(search)
          })
        .map((item)=>{return( <div key={post.id} style={{ border:"solid 1px black"}}>
    <p> {item.id}</p>
    <p> {item.title}</p>
    <p> {item.body}</p>
    <button onClick={()=>navigate(`${item.id.toString()}`)}>comment</button>
    </div>)})}</div>
  </ div>
   
  )
}
