import React, {useEffect, useState} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { eachproductData } from './action';
import {useParams} from 'react-router-dom'
import { getNumber } from './action';
import { basketAction } from './action';
export const Product = () => {
  const params = useParams();
  const eachproduct= useSelector((state)=>state.eachproduct)
  const {basket}= useSelector((state)=>state.basket)
  const[flag,setFlag]=useState("true")

  const dispatch=useDispatch()
  useEffect(()=>{
    dispatch(eachproductData(params))},[]
  )
  // function fn1() {
  //   dispatch(plusAction());
  // }
  
//  function fn3() {
//       setFlag="false";
//        }      
  return (
    <div>
      <p > {eachproduct.name}</p>
      <p > brand:{eachproduct.brand}</p>
      <p>  price: {eachproduct.price}$</p>
      <p > description:{eachproduct.description}</p>
      <img src={eachproduct.image} style={{ height:"300px", }}></img>
      <p>  count In Stock: {eachproduct.countInStock}</p>
      { eachproduct.countInStock?  <button 
      onClick={()=>{ 
        // fn1();
        dispatch(basketAction(basket));
        dispatch(getNumber())
      }}
      > add to basket</button>:
      <p> Is not available</p>
      }
      
     
    </div>
  )
 
}
