import React, {useEffect, useState} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { eachproductData } from './action';
import {useParams} from 'react-router-dom'
import { plusAction } from './action';
import { basketAction } from './action';
export const Product = () => {
  const params = useParams();
  const eachproduct= useSelector((state)=>state.eachproduct)
  const {basket}= useSelector((state)=>state.basket)
  const[flag,setFlag]=useState("true")

  const dispatch=useDispatch()
  useEffect(()=>{
    dispatch(eachproductData(params))},[]
  )
  function fn1() {
    dispatch(plusAction());
  }
  function fn2() {
        dispatch(basketAction(basket));
        } 
//  function fn3() {
//       setFlag="false";
//        }      
  return (
    <div>
      <p > {eachproduct.data.name}</p>
      {/* <p > brand:{eachproduct.data.brand}</p>
      <p>  price: {eachproduct.data.price}$</p>
      <p > description:{eachproduct.data.description}</p>
      <img src={eachproduct.data.image} style={{ height:"300px", }}></img> */}
      <p>  count In Stock: {eachproduct.data.countInStock}</p>
      { eachproduct.data.countInStock?  <button 
      onClick={()=>{ ;
        fn1();fn2();
      }}
      > add to basket</button>:
      <p> Is not available</p>
      }
     
    </div>
  )
 
}
