import React, {useEffect, useState} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { jobData , searchData} from './action';
import { useNavigate } from 'react-router-dom';

export const Home = () => {
  const dispatch=useDispatch()
  const job= useSelector((state)=>state.job)
  const [search, setSearch] = useState("");
  const [search1, setSearch1] = useState("");
  const [search2, setSearch2] = useState(true);
  const product=job.product
  const time=[]
  let now =new Date("2022-10-30T00:00:08.036Z").getTime();
  product.map((item,index)=>{return(  
  time.push(((now-new Date(item.postedAt).getTime())/3600000))  
  ) })
  const navigate=useNavigate();
  useEffect(()=>{  
   
     dispatch(jobData())
   },[]
  ) 

  return (<div>
  <header>
     <span className="material-icons-outlined">search</span>
      <input placeholder="filter by title,company"  onChange={(e) => setSearch(e.target.value.toLowerCase())} style={{ width:"450px" ,margin:"auto",borderRadius:"5px",borderColor:"lightgray",boxShadow:"5px 5px 5px 0px grey"}}>
      </input>
      
      <span class="material-icons">location_on</span>
      <input placeholder="filter by location"  onChange={(e) => setSearch1(e.target.value.toLowerCase())} style={{ width:"450px" ,margin:"auto",borderRadius:"5px",borderColor:"lightgray",boxShadow:"5px 5px 5px 0px grey"}}>
      </input>
      <input type="checkbox"  checked={search2} style={{marginRight:"5px",marginTop:"-5px"}} onChange=
       {()=>{
         setSearch2(!search2); 
       }} />
       <span >Full Time Only</span>
       <button onClick={()=>(
    dispatch(searchData(search,search1,search2)))}>serach</button>
     </header>
    {product.filter((item, index) => {
     return ( (item.position.toLowerCase().includes(search)) || (item.company.toLowerCase().includes(search))
      && (item.location.toLowerCase().includes(search1)) || (search2 && item.contract=="Full Time")
     )}).map((item, index) => {return( <div key={item.id} 
      style={{  height:"300px",textAlign:"center",border:"solid 1px black", margin:"20px",padding:"20px" }}>
  <img src={item.logo} style={{ height:"100px", }}></img>
  {time[index]<24 ? <p style={{ fontStyle:"italic"}}> {(time[index]).toFixed()} Hours ago </p>:
  (time[index]<168 ? <p style={{ fontStyle:"italic"}}> {(time[index]/24).toFixed()} Days ago </p>:
  (time[index]<504 ? <p style={{ fontStyle:"italic"}}> {(time[index]/168).toFixed()} Weeks ago </p>:
  <p style={{ fontStyle:"italic"}}> {(time[index]/720).toFixed()} Months ago </p>
  ))}
  
  <p style={{ fontStyle:"italic",fontWeight:"bold",fontSize:"14px"}}> {item.company}</p>
  <p style={{ fontStyle:"italic"}}> {item.contract} </p>
  <p style={{ fontStyle:"italic"}}> {item.location}</p>
  <p style={{ fontStyle:"italic"}}> {item.position}</p>

  </div>)})}
   
    
    <div style={{display:"grid", gridTemplateColumns:"1fr 1fr 1fr"}} >

        {product
        .map((item,index)=>{return( <div key={item.id} 
        style={{  height:"300px",textAlign:"center",border:"solid 1px black", margin:"20px",padding:"20px" }}>
    <img src={item.logo} style={{ height:"100px", }}></img>
    {time[index]<24 ? <p style={{ fontStyle:"italic"}}> {(time[index]).toFixed()} Hours ago </p>:
    (time[index]<168 ? <p style={{ fontStyle:"italic"}}> {(time[index]/24).toFixed()} Days ago </p>:
    (time[index]<504 ? <p style={{ fontStyle:"italic"}}> {(time[index]/168).toFixed()} Weeks ago </p>:
    <p style={{ fontStyle:"italic"}}> {(time[index]/720).toFixed()} Months ago </p>
    ))}
    
    <p style={{ fontStyle:"italic",fontWeight:"bold",fontSize:"14px"}}> {item.company}</p>
    <p style={{ fontStyle:"italic"}}> {item.contract} </p>
    <p style={{ fontStyle:"italic"}}> {item.location}</p>
    <p style={{ fontStyle:"italic"}}> {item.position}</p>

    </div>)})}
    </div>
    <button >LoadMore</button>
    </div>

  )
}
