import axios from "axios";

export const userData = () => async (dispatch, getState) => {
  dispatch({ type: "get_user_request" });
  try {
    const { data } = await axios.get(
      "https://jsonplaceholder.typicode.com/users"
    );
    dispatch({ type: "get_user_succes", payload: [...data] });
    console.log(data)
  } catch (error) {
    console.log(error.message);
    dispatch({ type: "get_user_failed", payload: error.message });
  }
};
export const postData = () => async (dispatch, getState) => {
  dispatch({ type: "get_post_request" });
  try {
    const { data } = await axios.get(
      "https://jsonplaceholder.typicode.com/posts"
    );
    dispatch({ type: "get_post_succes", payload: [...data] });
    console.log(data)
  } catch (error) {
    console.log(error.message);
    dispatch({ type: "get_post_failed", payload: error.message });
  }
};
export const commentData = () => async (dispatch, getState) => {
  dispatch({ type: "get_comment_request" });
  try {
    const { data } = await axios.get(
      "https://jsonplaceholder.typicode.com/comments"
    );
    dispatch({ type: "get_comment_succes", payload: [...data] });
    console.log(data)
  } catch (error) {
    console.log(error.message);
    dispatch({ type: "get_comment_failed", payload: error.message });
  }
};
