import axios from "axios";

export const productData = () => async (dispatch, getState) => {
  dispatch({ type: "get_product-request" });
  try {
    const { data } = await axios.get(
      "http://45.138.24.15:9000/api/products/"
    );
    localStorage.setItem("produsts", JSON.stringify(data));
    dispatch({ type: "get_product_succes", payload: [...data] });
    console.log(data)
  } catch (error) {
    console.log(error.message);
    dispatch({ type: "get_product_failed", payload: error.message });
  }
};
export const eachproductData = () => async (dispatch, getState) => {
  dispatch({ type: "get_eachproduct-request" });
  // const text = [...getState().products.id];
  try {
    const { data } = await axios.get(
      "http://45.138.24.15:9000/api/products/61af0ae4c971af479002de90"
    );
    dispatch({ type: "get_eachproduct_succes", payload: [...data] });
    console.log(data)
  } catch (error) {
    console.log(error.message);
    dispatch({ type: "get_eachproduct_failed", payload: error.message });
  }
};
