import React, {useState} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { userData } from './action';
import { changeuserData } from './action';
import { useNavigate } from 'react-router-dom';
export const Profile = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password,setPassword] = useState("");
  const dispatch=useDispatch()
  const {user}= useSelector((state)=>state.user)
  // const navigate=useNavigate();
  // console.log (user)
  const token= user.token
  
  return (
    <div >
    <button
     onClick={()=>{ 
      (dispatch(userData(token)))
       ;}}
    > get user Data </button>  
    <div style={{backgroundColor:"darkgray", height:"500px",margin:"50px"}}>
    <h3 style={{display:"grid", gridTemplateColumns:"1fr",textAlign:"center" ,margin:"20px 0",padding:"20px",backgroundColor:"gray"}}> Changing user data</h3>
    <div style={{backgroundColor:"white", width:"500px",margin:"20px auto", textAlign:"left",padding:"10px"}}>
    <div style={{display:"grid", gridTemplateColumns:"1fr 1fr",padding:"10px"}}>
     <lable > Name</lable>
    <input type="text" value={name} onChange= {(e) => {
              setName(e.target.value)}}></input>
    </div> 
    <div style={{display:"grid", gridTemplateColumns:"1fr 1fr",padding:"10px"}}>
    <lable > Email</lable>
    <input  type="email" value={email} onChange= {(e) => {
              setEmail(e.target.value)}}></input>
    </div>
    <div style={{display:"grid", gridTemplateColumns:"1fr 1fr",padding:"10px"}}>
    <lable > Password </lable>
    <input  type="password" placeholder='7-16 characters,first must be letter ' value={password} 
    onChange= {(e) => {
    setPassword(e.target.value)}}></input>
        
    </div>

    <div style={{textAlign:"center"}}>
    {( password && email && name ) &&  
    <button style={{backgroundColor:"black",color:"white",margin:"20px auto",padding:"5px 40px"}} 
     onClick={()=>{ 
      (dispatch(changeuserData(name,email, password,token)))
       ;}}
    > change user Data </button>}
    </div>
    </div>
    </div>
    </div>
  )
}
