import React, {useEffect} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { newOrder } from './action';
import { useNavigate } from 'react-router-dom';
import { computeHeadingLevel } from '@testing-library/react';
export const Final = () => {
  const basket = useSelector((state) =>state.basket);
  const price = useSelector((state) =>state.price);
  const dispatch=useDispatch()
  const navigate=useNavigate();
  
 console.log(order)
  return (<div>
     
     <button 
    //  onClick={()=>}
      style={{backgroundColor:"black",color:"white",margin:"20px auto",padding:"5px 40px"}}> done </button>
    <Table striped bordered hover>
<thead>
  <tr>
    <th>#</th>
    <th>name</th>
    <th>price</th>
    <th>QTY</th>
  </tr>
</thead>
<tbody>
  {basket
    .map((item, index) => (
      <tr key={index}>
        <td > {index + 1} </td>
        <td>{item.name}</td>
        <td>{item.price} $</td>
        <td >
          <div>
          { item.qty < item.countInStock && <button 
      onClick={()=>{ 
        dispatch(plusItem(basket,index));
        dispatch(getNumber())
        dispatch(getPrice())
         }}
      >+</button>
         }
        
        { item.qty>0 &&  <button 
         onClick={()=>{ 
        dispatch(minusItem(basket,index));
        dispatch(getNumber())
        dispatch(getPrice())
         ;}}
      >-</button >
         }
         <button onClick={()=>{
          dispatch(deleteItem(index))
          dispatch(getNumber())
          dispatch(getPrice())
        }}
          > X </button>
          <p> QTY: {item.qty}</p>
      
          </div>
        </td>
      </tr>
    ))}
</tbody>
</Table>
<div style={{display:"grid", gridTemplateColumns:"1fr" ,padding:"20px"}}>
<span style={{textAlign:"center" ,margin:"20px 0",padding:"20px",backgroundColor:"gray", border:"1px solid black"}}>   Total price is {price} $ </span>
</div>
    </div>)
}
