import React, {useEffect} from 'react';
import {  useSelector } from 'react-redux';
import {  Table } from "react-bootstrap";
import { newOrder } from './action';

export const Final = () => {
  const basket = useSelector((state) =>state.basket);
  const price = useSelector((state) =>state.price);
  const {order} = useSelector((state) =>state.order);
 console.log (order)
  return (<div style={{backgroundColor:"darkgray",margin:"50px"}}>
     
  
    <table style={{display:"grid", gridTemplateColumns:"1fr" ,margin:"20px"}}>
<tbody >
  {basket
    .map((item, index) => (
      <tr key={index} style={{ border:"1px dotted gray", }}>
        <td> <img src={item.image} style={{ height:"50px",margin:"10px" }}></img></td>
        <td >  Item {index + 1}--- </td>
        <td>{item.name} </td>
        <td>--- price: {item.price} $</td>
        <td  style={{padding:"20px"}} >---quantity: {item.qty}</td>
        
      </tr>
    ))}
</tbody>
</table>
<div style={{display:"grid", gridTemplateColumns:"1fr" ,padding:" 0 20px"}}>
<p>  Total price is : {price} $ </p>
<p> Shipping Address is : {order.shippingAddress?.city}- {order.shippingAddress?.address} </p>
<p> Phone : {order.shippingAddress?.phone}</p>

</div>
<button 
      style={{backgroundColor:"black",color:"white",margin:"0 20px",padding:"5px 40px", }} onClick={
        ()=> alert("Your shopping was done successfully")
      }> Done </button>
    </div>)
}
