import React, {useEffect, useState} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { newOrder } from './action';
import { useNavigate, useParams } from 'react-router-dom';

export const Adress = () => {
  const [adress, setAdress] = useState("");
  const [city, setCity] = useState("");
  const [postalcode,setPostalcode] = useState("");
  const [phone,setPhone] = useState("");
  const navigate=useNavigate();
  return (<>
   <div style={{backgroundColor:"darkgray", height:"500px",margin:"50px"}}>
    <h3 style={{display:"grid", gridTemplateColumns:"1fr",textAlign:"center" ,margin:"20px 0",padding:"20px",backgroundColor:"gray"}}> Post Information</h3>
    <form style={{backgroundColor:"white", width:"500px",margin:"20px auto", textAlign:"left",padding:"10px"}}>
    <div style={{display:"grid", gridTemplateColumns:"1fr 1fr",padding:"10px"}}>
    <lable > Adress</lable>
    <input  type="adress" value={adress} onChange= {(e) => {
              setAdress(e.target.value)}}></input>
    </div>
    <div style={{display:"grid", gridTemplateColumns:"1fr 1fr",padding:"10px"}}>
    <lable > City</lable>
    <input  type="name" value={city} onChange= {(e) => {
              setCity(e.target.value)}}></input>
    </div>
    <div style={{display:"grid", gridTemplateColumns:"1fr 1fr",padding:"10px"}}>
    <lable > Postal Code </lable>
    <input  type="number" value={postalcode} onChange= {(e) => {
              setPostalcode(e.target.value)}}></input>
    </div>
    <div style={{display:"grid", gridTemplateColumns:"1fr 1fr",padding:"10px"}}>
    <lable > Phone Number </lable>
    <input  type="Number" value={phone} onChange= {(e) => {
              setPhone(e.target.value)}}></input>
    </div>
    <div style={{textAlign:"center"}}>
     <button onClick={()=>{
      dispatch(newOrder(adress,city,postalcode,phone)),
      navigate("./final")}} style={{backgroundColor:"black",color:"white",margin:"20px auto",padding:"5px 40px"}}> next </button>
    </div>
   </form>
     </div>
  </>
   
  )
}
