import React, {useEffect} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { postData, userData } from './action';
import { useNavigate, useParams } from 'react-router-dom';

export const Home = () => {
  const dispatch=useDispatch()
  const {post}= useSelector((state)=>state.post)
  const {user}= useSelector((state)=>state.user)
  const navigate=useNavigate();
  const params=useParams();
  useEffect(()=>{
    dispatch(postData())},[]
  )
  useEffect(()=>{
    dispatch(userData())},[]
  )
  return (<div style={{display:"grid", gridTemplateColumns:"1fr 1fr"}}>
    <div  >
        {user
        // .filter(
        //     (item)=>{return +item.userId === +params.postid})
        .map((item)=>{return( <div key={post.id} style={{ border:"solid 1px black"}}>
    <p> {item.id}</p>
    <p> {item.name}</p>
    
    <button onClick={()=>navigate(`${item.id.toString()}`)}>comment</button>
    </div>)})}</div>
   <div > 
        {post
        // .filter(
        //     (item)=>{return +item.userId === +params.postid})
        .map((item)=>{return( <div key={post.id} style={{ border:"solid 1px black"}}>
    <p> {item.id}</p>
    <p> {item.title}</p>
    <p> {item.body}</p>
    <button onClick={()=>navigate(`${item.id.toString()}`)}>comment</button>
    </div>)})}</div>
  </ div>
   
  )
}
