import React from 'react';
import "./bootstrap.css";
import {  Table } from "react-bootstrap";
import {  useDispatch,useSelector } from 'react-redux';
import {getNumber, loginUser} from './action';
import {getPrice} from './action';
import {deleteItem} from './action';
import {plusItem} from './action';
import {minusItem} from './action';
import { useNavigate, useParams } from 'react-router-dom';


export const Basket = () => {

  const basket = useSelector((state) =>state.basket);
  const price = useSelector((state) =>state.price);
  const {user}= useSelector((state) =>state.user);
  const number = useSelector((state) =>state.number);
  const dispatch=useDispatch()
  const navigate=useNavigate();
  
  
  return (
    <div>
    <Table striped bordered hover>
<thead>
  <tr>
    <th style={{textAlign:"center"}}>#</th>
    <th>Name</th>
    <th>Price</th>
    <th>Quantity</th>
  </tr>
</thead>
<tbody>
  {basket
    .map((item, index) => (
      <tr key={index}>
        <td style={{textAlign:"center"}}> {index + 1} </td>
        <td>{item.name}</td>
        <td>{item.price} $</td>
        <td >
          <div>
          { item.qty < item.countInStock && <button 
      onClick={()=>{ 
        dispatch(plusItem(basket,index));
        dispatch(getNumber())
        dispatch(getPrice())
         }}
      >+</button>
         }
        
        { item.qty>0 &&  <button 
         onClick={()=>{ 
        dispatch(minusItem(basket,index));
        dispatch(getNumber())
        dispatch(getPrice())
         ;}}
      >-</button >
         }
         <button onClick={()=>{
          dispatch(deleteItem(index))
          dispatch(getNumber())
          dispatch(getPrice())
        }}
          > X </button>
          <p> QTY: {item.qty}</p>
      
          </div>
        </td>
      </tr>
    ))}
</tbody>
</Table>
<div style={{display:"grid", gridTemplateColumns:"1fr" ,padding:"20px"}}>
<span style={{textAlign:"center" ,margin:"20px 0",padding:"20px",backgroundColor:"gray", border:"1px solid black", fontStyle:"italic",fontWeight:"bold"}}>   Total price is {price} $ </span>
</div>
{ number ==0 ?  <p> your basket is empty </p>: user.id ? <button onClick={
  ()=>{
    navigate("./adress");
}}
   style={{margin:"20px auto", float:"right"}}> next </button> :

<button onClick={()=>navigate("./login")} style={{margin:"20px auto",float:"right"}}> login </button>}
    </div>
  )
}
