export const userReducer = (
  state = { user: [],  error: "" },
  action
) => {
  switch (action.type) {
    case "get_user_succes":
      return { ...state, user: action.payload, error: false };
    case "get_user_failed":
      return { ...state, error: action.payload };
    default:
      return state;
  }
};
