import React from 'react';
import "./bootstrap.css";
import {  Table } from "react-bootstrap";
import {  useDispatch,useSelector } from 'react-redux';
import {getNumber} from './action';
import {getPrice} from './action';
import {deleteItem} from './action';
import {plusItem} from './action';
import {minusItem} from './action';
import { useNavigate, useParams } from 'react-router-dom';


export const Basket = () => {

  const basket = useSelector((state) =>state.basket);
  // const number = useSelector((state) =>state.number);
  const price = useSelector((state) =>state.price);
  const dispatch=useDispatch()
  const navigate=useNavigate();
  
  
  return (
    <div>
    <Table striped bordered hover>
<thead>
  <tr>
    <th>#</th>
    <th>name</th>
    <th>price</th>
    <th>QTY</th>
  </tr>
</thead>
<tbody>
  {basket
    .map((item, index) => (
      <tr key={index}>
        <td > {index + 1} </td>
        <td>{item.name}</td>
        <td>{item.price} $</td>
        <td >
          <div>
          { item.qty < item.countInStock && <button 
      onClick={()=>{ 
        dispatch(plusItem(basket,index));
        dispatch(getNumber())
        dispatch(getPrice())
         }}
      >+</button>
         }
        
        { item.qty>0 &&  <button 
         onClick={()=>{ 
        dispatch(minusItem(basket,index));
        dispatch(getNumber())
        dispatch(getPrice())
         ;}}
      >-</button >
         }
         <button onClick={()=>{
          dispatch(deleteItem(index))
          dispatch(getNumber())
          dispatch(getPrice())
        }}
          > X </button>
          <p> QTY: {item.qty}</p>
      
          </div>
        </td>
      </tr>
    ))}
</tbody>
</Table>
<div style={{display:"grid", gridTemplateColumns:"1fr" ,padding:"20px"}}>
<span style={{textAlign:"center" ,margin:"20px 0",padding:"20px",backgroundColor:"gray", border:"1px solid black"}}>   Total price is {price} $ </span>
</div>
{/* yek dokme bashe,age login bud bere adress,agarna bere login */}
<button onClick={
  ()=>{
    navigate("./adress");
}}
   style={{backgroundColor:"black",color:"white",margin:"20px auto",padding:"5px 40px"}}> next </button>
<button onClick={()=>navigate("./login")} style={{backgroundColor:"black",color:"white",margin:"20px auto",padding:"5px 40px"}}> login </button>
    </div>
  )
}
