import React, {useEffect} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { myOrder } from './action';
import { useNavigate } from 'react-router-dom';
export const Orders = () => {
  const dispatch=useDispatch()
  const {order}= useSelector((state)=>state.order)
  console.log(order)
  const navigate=useNavigate();
  useEffect(()=>{
    dispatch(myOrder())},[]
  )
  return(
       <div style={{display:"grid", gridTemplateColumns:" 1fr"}} >
  {order
  .map((item)=>{return( <div key={item.index} style={{ border:"solid 1px black", margin:"30px"}}>
 <span style={{ fontStyle:"italic"}} > Order ID: </span>  
<span > {item._id}</span>
<span> ----------Total Price: {item.totalPrice.toFixed(2)}</span>
<button style={{float:"right"}} onClick={()=>navigate(`${item._id}`)

}>View Details</button>

</div>)})}</div>

  )}
