import React,{useEffect} from 'react'
import { Link } from 'react-router-dom'
import {useDispatch, useSelector}from 'react-redux'
import { Dropdown, DropdownButton} from "react-bootstrap"

const Header = () => {
  const number = useSelector((state) =>state.number);
  const {user} = useSelector((state) =>state.user);
  const dispatch=useDispatch
  console.log(user)

  return (
    <div style={{display:"flex" ,justifyContent:"space-around"}}>
        <Link to="/"> home </Link>
        <div>
          <Link to="basket"> 
            <span className='material-icons-two-tone'> add_shopping_cart</span>
          </Link>
        <span> {number} </span>
        </div>
        <div >
        { user.id ?
        <DropdownButton id="dropdown-basic-button" title={user.email}>
        <Dropdown.Item href="#/setting">Setting</Dropdown.Item>
        <Dropdown.Item href="#/Order">Orders</Dropdown.Item>
        <Dropdown.Item    onClick={()=> {
          localStorage.removeItem("users");
           window.location.href = '/';
           }}>logOut</Dropdown.Item>
      </DropdownButton>
      //   <button style={{backgroundColor:"black",color:"white",margin:"20px auto",padding:"5px 40px"}} 
     
      // > log out</button>
        // <Link to="profile"> {user.email} </Link>
         :  <Link to="login"> login </Link> }   
        </div>
    </div>
  )
}

export default Header
