import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { plusAction } from "./action";

const Plus = () => {
  const dispatch = useDispatch();
  const { num } = useSelector((state) => state.plus);
  return (
    <>
      <button
        onClick={() => {
          // dispatch({ type: "plus10", payload: num + 10 });
          dispatch(plusAction(+10));
        }}
      >
        +10
      </button>
      <button
        onClick={() => {
          // dispatch({ type: "plus5", payload: num + 5 });
          dispatch(plusAction(+5));
        }}
      >
        +5
      </button>
      <button
        onClick={() => {
          // dispatch({ type: "minus10", payload: num - 10 });
          dispatch(plusAction(-10));
        }}
      >
        -10
      </button>
      <button
        onClick={() => {
          // dispatch({ type: "minus5", payload: num - 5 });
          dispatch(plusAction(-5));
        }}
      >
        -5
      </button>
    </>
  );
};

export default Plus;
