import React, {useEffect} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { productData } from './action';
import { useNavigate, useParams } from 'react-router-dom';


export const Home = () => {

  const dispatch=useDispatch()
  const {product}= useSelector((state)=>state.product)
  const navigate=useNavigate();
  // const params=useParams();
  useEffect(()=>{
    dispatch(productData())},[]
  )

  return (
    <div style={{display:"grid", gridTemplateColumns:"1fr 1fr 1fr"}} >
        {product
        .map((item)=>{return( <div key={product.id} style={{ border:"solid 1px black", margin:"30px"}}>
    <p > {item.name}</p>
    <img src={item.image} style={{ height:"20px", width:""}}></img>
    <button onClick={()=>navigate(`${item.id.toString()}`)}>more information</button>
    </div>)})}</div>
  
  )
}
