import { useDispatch, useSelector } from 'react-redux';
import { jobData } from './action';
import { useNavigate } from 'react-router-dom';

export const Home = () => {
  const dispatch=useDispatch()
  const job= useSelector((state)=>state.job)
  const product=job.product
  
  let now =new Date("2022-10-14T09:00:08.036Z").getTime();
  // ("2022-10-14T09:00:08.036Z")
  let jobIssue=new Date("2022-10-13T20:00:08.036Z").getTime();
  console.log(now)
  console.log((now-jobIssue)/3600000)
 if((now-jobIssue)/3600000 < 24){
   console.log(((now-jobIssue)/3600000)+" Hours ago")
 }
  // if (now.getFullYear() != jobIssue.getFullYear()){
  //   console.log((now.getFullYear()-jobIssue.getFullYear())+" years ago") 
  // }else if(now.getMonth() != jobIssue.getMonth()){
  //   console.log((now.getMonth()-jobIssue.getMonth())+" months ago") }
  // else if(now.getDate() != jobIssue.getDate()){
  //   console.log((-now.getDate()+jobIssue.getDate())+" days ago") } 
  //   else {console.log((-now.getHours()+jobIssue.getHours())+" hours ago")}
  
  const navigate=useNavigate();
  useEffect(()=>{  
   
     dispatch(jobData())
   },[]
  ) 

  return (
    
    <div style={{display:"grid", gridTemplateColumns:"1fr 1fr 1fr"}} >
        {product
        .map((item,index)=>{return( <div key={item.id} 
        style={{  height:"300px",textAlign:"center",border:"solid 1px black", margin:"20px",padding:"20px" }}>
    <img src={item.logo} style={{ height:"100px", }}></img>
    <p style={{ fontStyle:"italic"}}> {item.postedAt}</p>
    <p style={{ fontStyle:"italic",fontWeight:"bold",fontSize:"14px"}}> {item.company}</p>
    <p style={{ fontStyle:"italic"}}> {item.contract} </p>
    <p style={{ fontStyle:"italic"}}> {item.location}</p>
    <p style={{ fontStyle:"italic"}}> {item.position}</p>

    </div>)})}
    </div>
  
  )
}
