import React, {useEffect, useState} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { product } from './action';
import { useNavigate, useParams } from 'react-router-dom';


export const Home = () => {

  const dispatch=useDispatch()
  const {product}= useSelector((state)=>state.product)
  const navigate=useNavigate();
  const params=useParams();
  useEffect(()=>{
    dispatch(product())},[]
  )

  return (<div style={{display:"grid", gridTemplateColumns:"1fr 1fr"}}>
    <div  >
      
        {product
        .map((item)=>{return( <div key={product.id} style={{ border:"solid 1px black", margin:"30px"}}>
    <p > {item.name}</p>
    <img src={item.image}></img>
    <button onClick={()=>navigate(`${item.id.toString()}`)}>post</button>
    </div>)})}</div>
  </ div>
   
  )
}
