import React, {useEffect} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { eachorderData } from './action';
import {useParams} from 'react-router-dom'
import { useNavigate } from 'react-router-dom';
import { computeHeadingLevel } from '@testing-library/react';
export const Eachorder = () => {
    const params = useParams();
    const {eachorder}= useSelector((state)=>state.eachorder)
    console.log(eachorder)
  
    const dispatch=useDispatch()
  useEffect(()=>{
    dispatch(eachorderData(params))},[]
  )
  return (<div>
     
     <button 
    //  onClick={()=>}
      style={{backgroundColor:"black",color:"white",margin:"20px auto",padding:"5px 40px"}}> each order </button>
<p > {eachorder.totalPrice}</p>
<p > {eachorder.shippingAddress.address}</p>
<p > {eachorder.shippingAddress.phone}</p>

  <div style={{display:"grid", gridTemplateColumns:"1fr 1fr 1fr 1fr 1fr"}}>
   {eachorder.orderItems.map((item,index)=>{return( <div style={{ border:"solid 1px black"}}>
   <p> {item.name}</p>
    <p> {item.price}</p>
   <p> {item.qty}</p>
  </div>)})}</div>
    </div>)
}
