import React, {useEffect, useState} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { eachproductData } from './action';
import {useParams} from 'react-router-dom'
import { getNumber } from './action';
import { basketAction } from './action';
import { getPrice } from './action';
import { computeHeadingLevel } from '@testing-library/react';
export const Product = () => {
  const params = useParams();
  const eachproduct= useSelector((state)=>state.eachproduct)
  const basket= useSelector((state)=>state.basket)

  const dispatch=useDispatch()
  useEffect(()=>{
    dispatch(eachproductData(params))},[]
  )
  const check= basket.findIndex(basket => basket.name === eachproduct.name)
  return (
    <div>
      <p > {eachproduct.name}</p>
      <p > brand:{eachproduct.brand}</p>
      <p>  price: {eachproduct.price}$</p>
      <p > description:{eachproduct.description}</p>
      <img src={eachproduct.image} style={{ height:"300px", }}></img>
      <p>  count In Stock: {eachproduct.countInStock}</p>
      
{/*    
         {check !== -1 || !eachproduct.countInStock ? <p> Is already in the basket or Is not available</p> :<button 
      onClick={()=>{ 
        dispatch(basketAction(basket));
        dispatch(getNumber())
        dispatch(getPrice())
      }}
      > add to basket</button> } */}
       { eachproduct.countInStock ?  <button 
      onClick={()=>{ 
        dispatch(basketAction(basket));
        dispatch(getNumber())
        dispatch(getPrice())
      }}
      > add to basket</button> :
      <p> Is not available</p> }
       {check === -1  ? <button 
      onClick={()=>{ 
        dispatch(basketAction(basket));
        dispatch(getNumber())
        dispatch(getPrice())
      }}
      > add to basket</button> : <p> Is already in the basket </p> }
     
    </div>
  )
 
}
