export const productReducer = (
  state = { product: [],  error: "" },
  action
) => {
  switch (action.type) {
    case "get_product_succes":
      return { ...state, product: action.payload, error: false };
    case "get_product_failed":
      return { ...state, error: action.payload };
    default:
      return state;
  }
};

export const postReducer = (
  state = { post: [],  error: "" },
  action
) => {
  switch (action.type) {
    case "get_post_succes":
      return { ...state, post: action.payload, error: false };
    case "get_post_failed":
      return { ...state, error: action.payload };
    default:
      return state;
  }
};
export const commentReducer = (
  state = { comment: [],  error: "" },
  action
) => {
  switch (action.type) {
    case "get_comment_succes":
      return { ...state, comment: action.payload, error: false };
    case "get_comment_failed":
      return { ...state, error: action.payload };
    default:
      return state;
  }
};
