import React from 'react';
import {  useDispatch,useSelector } from 'react-redux';
import { plusNumber } from './action';
import {minusNumber} from './action';


export const Basket = () => {

  const product = useSelector((state) =>state.basket);
  const dispatch=useDispatch()
  console.log(product.basket)

  return (
    <div>
      {product.basket
        .map((item)=>{return( <div >
     <p > name: {item.x}</p>
    <p > price: {item.y} $</p>
    <p> QTY: {number}</p>
    
    <button 
      onClick={()=>{  dispatch(plusNumber())}}
      >+</button>
   <button 
      onClick={()=>{  dispatch(minusNumber())}}
      >-</button>
    
    </div>)})}
     
      <button> next </button>
  
    </div>
  )
}
