import axios from "axios";

export const getData = () => async (dispatch, getState) => {
  dispatch({ type: "get_todo_request" });
  try {
    const { data } = await axios.get(
      "https://jsonplaceholder.typicode.com/todos"
    );
    localStorage.setItem("todo", JSON.stringify(data));
    dispatch({ type: "get_todo_succes", payload: [...data] });
  } catch (error) {
    console.log(error.message);
    dispatch({ type: "get_todo_failed", payload: error.message });
  }
};

export const changeData = (index, value) => (dispatch, getState) => {
  console.log(getState)
  const text = [...getState().post.post];
  text[index].body = value;
  localStorage.setItem("todo", JSON.stringify(text));
  dispatch({ type: "changeTodo", payload: [...text] });
};
