export const productReducer = (
  state = { product: [],  error: "" },
  action
) => {
  switch (action.type) {
    case "get_product_succes":
      return { ...state, product: action.payload, error: false };
    case "get_product_failed":
      return { ...state, error: action.payload };
    default:
      return state;
  }
};

export const eachproductReducer = (
  state = { eachproduct: {},  error: "" ,num: 0, price:"" ,name:""},
  action
) => {
  switch (action.type) {
    case "get_eachproduct_succes":
      return   action.payload;
    case "get_eachproduct_failed":
      return   action.payload 
    default:
      return state;
  }
};
export const basketReducer = (state = { num: 0  ,basket:[] }, action) => {
  switch (action.type) {
    case "plus":
      return { ...state, num: action.payload };
    // case "price":
    //   return  {...state, price: action.payload }; 
    //   case "name":
    //     return  {...state, name: action.payload };  
    case "basket":
          return  {...state, name: action.payload };  
    default:
      return state;
  }
};
