import React, {useEffect} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { productData } from './action';
import { useNavigate } from 'react-router-dom';
import { newUser } from './action';

export const Home = () => {

  const dispatch=useDispatch()
  const {product}= useSelector((state)=>state.product)
  const navigate=useNavigate();
  useEffect(()=>{
    dispatch(productData())},[]
  )
  // useEffect(()=>{
  //   dispatch(newUser ())},[]
  // )
  

  return (
    
    <div style={{display:"grid", gridTemplateColumns:"1fr 1fr 1fr"}} >
        {product
        .map((item)=>{return( <div key={product.index} style={{ border:"solid 1px black", margin:"30px"}}>
    <img src={item.image} style={{ height:"100px", }}></img>
    <p > {item.name}</p>
    <p > price: {item.price} $</p>
    <p > rating: {item.rating}</p>
    <button onClick={()=>navigate(`${item._id}`)}>more information</button>
    </div>)})}</div>
  
  )
}
