import { createStore, combineReducers, applyMiddleware } from "redux";
import thunk from "redux-thunk";
import { composeWithDevTools } from "redux-devtools-extension";
import {productReducer, eachproductReducer, basketReducer ,userReducer,numberReducer,
  priceReducer,orderReducer,eachorderReducer,userdataReducer} from "./reducer";
const reducers = combineReducers({
  product: productReducer,
  eachproduct: eachproductReducer,
  basket: basketReducer,
  user: userReducer,
  number: numberReducer,
  price: priceReducer,
  order: orderReducer,
  eachorder:eachorderReducer,
  userdata: userdataReducer

});
const middleware = [thunk];
const localstorageproducts = JSON.parse(localStorage.getItem("products")) 
  ? JSON.parse(localStorage.getItem("products"))
  : [];
  const localstorageusers = JSON.parse(localStorage.getItem("users")) 
  ? JSON.parse(localStorage.getItem("users"))
  : {}; 
const initialState = {
 product: { product: localstorageproducts,  error: "" },
 user:{ user: localstorageusers,  error: "" }
};
const store = createStore(
  reducers,
  initialState,
  applyMiddleware(...middleware)
);

export default store;
