const totalPrice= getState().price
const name= getState().basket [0].x
const price= getState().basket[0].y
const countInStock= getState().basket[0].e
const qty= getState().basket[0].z
const id= getState().basket[0].id
const image= getState().basket[0].img
const token= getState().user.user.token
console.log(getState().basket[0])
 try {
