export const productReducer = (
  state = { product: [],  error: "" },
  action
) => {
  switch (action.type) {
    case "get_product_succes":
      return { ...state, product: action.payload, error: false };
    case "get_product_failed":
      return { ...state, error: action.payload };
    default:
      return state;
  }
};

export const eachproductReducer = (
  state = { eachproduct: {},  error: "" },
  action
) => {
  switch (action.type) {
    case "get_eachproduct_succes":
      return   action.payload;
    case "get_eachproduct_failed":
      return   action.payload 
    default:
      return state;
  }
};
export const plusReducer = (state = 0, action) => {
  switch (action.type) {
    case "plus":
      return   state+1;
    default:
      return state;
  }
};
