import React, {useEffect} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { jobData } from './action';
import { useNavigate } from 'react-router-dom';

export const Home = () => {
  const dispatch=useDispatch()
  const job= useSelector((state)=>state.job)
  const product=job.product
console.log(product)
  const navigate=useNavigate();
  useEffect(()=>{  
    if(!product.length){
     dispatch(jobData())}
   },[]
  ) 

  return (
    
    <div style={{display:"grid", gridTemplateColumns:"1fr 1fr 1fr"}} >
        {product
        .map((item,index)=>{return( <div key={item.id} 
        style={{  height:"300px",textAlign:"center",border:"solid 1px black", margin:"20px",padding:"20px" }}>
    <img src={item.logo} style={{ height:"100px", }}></img>
    <p style={{ fontStyle:"italic",fontWeight:"bold",fontSize:"14px"}}> {item.company}</p>
    <p style={{ fontStyle:"italic"}}> {item.contract} $</p>
    <p style={{ fontStyle:"italic"}}> {item.location}</p>
    {/* <button style={{ fontStyle:"italic",padding:"0"}} onClick={()=>navigate(`${item._id}`)}>More Information</button> */}
    </div>)})}
    </div>
  
  )
}
