import React, {useEffect, useState} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { jobData , searchData, emptyFilter, searchFulltime} from './action';

export const Home = () => {
  const dispatch=useDispatch()
  const job= useSelector((state)=>state.job)
  const filtered= useSelector((state)=>state.filter)
  const [search, setSearch] = useState(true);
  const [search1, setSearch1] = useState("");
  const [search2, setSearch2] = useState("");
  const [page,setPage]= useState("");
  const [product,setProduct]=useState([])
  const time=[]
  console.log(job)
  useEffect(()=>{  
    setProduct(job.product)
     dispatch(jobData())
   },[]
  ) 
  // useEffect(() => {
  //   async function getUsers() {
  //     const response = await fetch('https://randomuser.me/api/', {
  //       method: 'GET',
  //       headers: {
  //         accept: 'application/json',
  //       },
  //     });

  //     const data = await response.json();

  //     setUsers(data.results);
  //   }

  //   getUsers();
  // }, []);

  let now =new Date("2022-10-30T00:00:08.036Z").getTime();
  product.map((item)=>{return(  
  time.push(((now-new Date(item.postedAt).getTime())/3600000))  
  ) })
  return (<div className='containerDiv'>
    {(filtered.length) ?  <div className='cards'>
       {filtered.map((item,index)=>{return( <div key={item.id} 
       className="card">
    <img src={item.logo} ></img>
    {time[index]<24 ? <span > {(time[index]).toFixed()} Hours ago </span>:
    (time[index]<168 ? <span > {(time[index]/24).toFixed()} Days ago </span>:
    (time[index]<504 ? <span > {(time[index]/168).toFixed()} Weeks ago </span>:
    <span> {(time[index]/720).toFixed()} Months ago </span>
    ))}
    <span> , </span>
    <span > {item.contract} </span>
    <p className='position' > {item.position}</p>
    <p > {item.company}</p>
    <p className='location' > {item.location}</p>
    </div>)})
    } <button onClick={()=>{
      dispatch(emptyFilter())
      setSearch1("");
      setSearch2("");
      setSearch(false);
         }}> Back </button> </div> 
    : <div> <header>
      <div><span className="material-icons-outlined">search</span>
      <input placeholder="filter by title,company"  onChange={(e) => 
        setSearch1(e.target.value.toLowerCase())}>
      </input></div>
      <div><span class="material-icons">location_on</span>
      <input placeholder="filter by location"  onChange={(e) =>
         setSearch2(e.target.value.toLowerCase())} >
      </input></div>
       <div className='search'>
         <div> <input type="checkbox" className='check' checked={search} onChange=
       {()=>{
         setSearch(!search); 
       }} />
         <span className='fulltime'>Full Time Only</span></div>
       <button onClick={()=>
       {{search ? dispatch(searchFulltime(search1,search2))
        :dispatch(searchData(search1,search2))
       }}}
       >search</button></div> 
     </header>
    <div className='cards' >
        {product.slice(0,page+6)
        .map((item,index)=>{return( <div key={item.id} 
          className="card">
    <img src={item.logo} ></img>
    <div>
    {time[index]<24 ? <span > {(time[index]).toFixed()} Hours ago </span>:
    (time[index]<168 ? <span > {(time[index]/24).toFixed()} Days ago </span>:
    (time[index]<504 ? <span > {(time[index]/168).toFixed()} Weeks ago </span>:
    <span > {(time[index]/720).toFixed()} Months ago </span>
    ))}
    <span> , </span>
       <span> {item.contract} </span> 
    </div> 
       <p className='position'> {item.position}</p>
    <p > {item.company}</p>
    <p  className='location'> {item.location}</p>
    </div>)})}
    </div>
    <button className='loadmore' onClick=
       {()=>{
         setPage(page+6); 
       }}>LoadMore</button> </div> }
    </div>

  )
}
