export const jobReducer = (
  state = { job: [],  error: "" },
  action
) => {
  switch (action.type) {
    case "get_job_succes":
      return { ...state, product: action.payload, error: false };
    case "get_job_failed":
      return { ...state, error: action.payload };
     
    default:
      return state;
  }
};

export const filterReducer = (
  state = [],
  action
) => {
  switch (action.type) {
    case "search":
      return  action.payload;
    case "emptyFilter"  :
        return  action.payload ;  
    default:
      return state;
  }
};
