import React from 'react';
import "./bootstrap.css";
import {  Table } from "react-bootstrap";
import {  useDispatch,useSelector } from 'react-redux';
import {getNumber} from './action';
import {deleteItem} from './action';
import {plusItem} from './action';
import {minusItem} from './action';
import { useNavigate, useParams } from 'react-router-dom';


export const Basket = () => {

  const basket = useSelector((state) =>state.basket);
  // const number = useSelector((state) =>state.number);
  const dispatch=useDispatch()
  const navigate=useNavigate();
  
  
  return (
    <div>
    <Table striped bordered hover>
<thead>
  <tr>
    <th>#</th>
    <th>name</th>
    <th>price</th>
    <th>QTY</th>
  </tr>
</thead>
<tbody>
  {basket
    .map((item, index) => (
      <tr key={index}>
        <td > {index + 1} </td>
        <td>{item.x}</td>
        <td>{item.y} $</td>
        <td >
          <div>
          { item.z < item.e && <button 
      onClick={()=>{ 
        // item.z=item.z+1;
        dispatch(plusItem(basket,index));
        dispatch(getNumber())
         }}
      >+</button>
         }
        
        { item.z>0 &&  <button 
         onClick={()=>{ 
        dispatch(minusItem(basket,index));
        dispatch(getNumber())
         ;}}
      >-</button >
         }
         <button onClick={()=>{
          dispatch(deleteItem(index))
          dispatch(getNumber())
        }}
          > X </button>
          <p> QTY: {item.z}</p>
      
          </div>
        </td>
      </tr>
    ))}
</tbody>
</Table>
{/* yek dokme bashe,age login bud bere adress,agarna bere login */}
<button onClick={
  ()=>{navigate("./adress");
  
}}
   style={{backgroundColor:"black",color:"white",margin:"20px auto",padding:"5px 40px"}}> next </button>
<button onClick={()=>navigate("./login")} style={{backgroundColor:"black",color:"white",margin:"20px auto",padding:"5px 40px"}}> login </button>
    </div>
  )
}
