import axios from "axios";
export const productData = () => async (dispatch, getState) => {
  try {
    const { data } = await axios.get(
      "http://45.138.24.15:9000/api/products/"
    );
    localStorage.setItem("produsts", JSON.stringify(data));
    dispatch({ type: "get_product_succes", payload: [...data] });
    
  } catch (error) {
    console.log(error.message);
    dispatch({ type: "get_product_failed", payload: error.message });
  }
};
export const eachproductData = (params) => async (dispatch, getState) => {
  try {
    const data  = await axios.get(
      `http://45.138.24.15:9000/api/products/${params.id}`
    );
    dispatch({ type: "get_eachproduct_succes", payload: data});
  } 
  catch (error) {
    dispatch({ type: "get_eachproduct_failed", payload: error.message });
  }
};

export const plusAction = (index) => (dispatch, getState) => {
  console.log(getState().basket.basket[index].z)
  // const last = getState().basket.basket[index].z;
  // dispatch({ type: "plus", payload: 1 + last }); 
};

export const basketAction = (basket) => (dispatch, getState) => {
  const x= getState().eachproduct.data.name
  const y= getState().eachproduct.data.price
  const z= getState().basket.num;
  const help={x,y,z}
  console.log(help)
  dispatch({ type: "basket", payload: [...basket,help] });
};
