import React, {useEffect} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { eachorderData } from './action';
import {useParams} from 'react-router-dom'
import { useNavigate } from 'react-router-dom';
import { computeHeadingLevel } from '@testing-library/react';
export const Eachorder = () => {
    const params = useParams();
    const {eachorder}= useSelector((state)=>state.eachorder)
    console.log(eachorder)
    const shippingAddress = eachorder.shippingAddress
  
    const dispatch=useDispatch()
  useEffect(()=>{
    dispatch(eachorderData(params))},[]
  )
  return (<div>
    
<p > totalPrice: {eachorder.totalPrice}$</p>
<p > adress: {shippingAddress.address}</p>
<p > phone: {shippingAddress.phone}</p>

  <div style={{display:"grid", gridTemplateColumns:"1fr 1fr 1fr 1fr 1fr"}}>
   {eachorder.orderItems.map((item,index)=>{return( <div style={{ border:"solid 1px black"}}>
   <p> {item.name}</p>
    <p> price:  {item.price} $</p>
   <p> qty: {item.qty}</p>
  </div>)})}</div>
    </div>)
}
