import React, {useEffect, useState} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { newUser } from './action';
import { loginUser } from './action';
import { userData } from './action';
import { useNavigate } from 'react-router-dom';
import { computeHeadingLevel } from '@testing-library/react';

export const Signup = () => {
    const [name1, setName] = useState("");
    const [email1, setEmail] = useState("");
    const [password1,setPassword] = useState("");
    const [rePassword,setrePassword] = useState("");
    const dispatch=useDispatch()
  return (
    <div style={{backgroundColor:"darkgray", height:"500px",margin:"50px"}}>
    <h3 style={{display:"grid", gridTemplateColumns:"1fr",textAlign:"center" ,margin:"20px 0",padding:"20px",backgroundColor:"gray"}}> Registration form</h3>
    {/* <form style={{backgroundColor:"white", width:"500px",margin:"20px auto", textAlign:"left",padding:"10px"}}> */}
    <div style={{display:"grid", gridTemplateColumns:"1fr 1fr",padding:"10px"}}>
     <lable > Name</lable>
    <input type="text" value={name1} onChange= {(e) => {
              setName(e.target.value)}}></input>
    </div> 
    <div style={{display:"grid", gridTemplateColumns:"1fr 1fr",padding:"10px"}}>
    <lable > Email</lable>
    <input  type="email" value={email1} onChange= {(e) => {
              setEmail(e.target.value)}}></input>
    </div>
    <div style={{display:"grid", gridTemplateColumns:"1fr 1fr",padding:"10px"}}>
    <lable > Password </lable>
    <input  type="password" placeholder='7-16 characters,first must be letter ' value={password1} 
    onChange= {(e) => {
    setPassword(e.target.value)}}></input>
              {/* var passw=  /^[A-Za-z]\w{7,14}$/;
              if(inputtxt.value.match(passw))  */}
    </div>
    <div style={{display:"grid", gridTemplateColumns:"1fr 1fr",padding:"10px"}}>
    <lable > Retype Password</lable>
    <input type="password" value={rePassword} onChange= {(e) => {
              setrePassword(e.target.value)}}></input>
    </div>
    <div style={{textAlign:"center"}}>
     
    <button style={{backgroundColor:"black",color:"white",margin:"20px auto",padding:"5px 40px"}} 
    onClick={()=>{ 
      dispatch(newUser(name1,email1,password1))
       ;}}
    > sign up </button>
    </div>
    {/* </form> */}
    </div>
  )
}
