import React from 'react';
import "./bootstrap.css";
import {  Table } from "react-bootstrap";
import {  useDispatch,useSelector } from 'react-redux';
import { plusNumber } from './action';
import {minusNumber} from './action';
import {deleteNumber} from './action';


export const Basket = () => {

  const product = useSelector((state) =>state.basket);
  const number = useSelector((state) =>state.number);
  const dispatch=useDispatch()
  // if (!number) {
  //   // setPage((last) => last - 1);
  //   dispatch(deleteItem());
  // }


  return (
    <div>
      {product.basket
        .map((item,index)=>{return( <div >
     <p > name: {item.x}</p>
    <p > price: {item.y} $</p>
    
    
    </div>)})}
     
      <button> next </button>
  
    </div>
  )
}
<Table striped bordered hover>
<thead>
  <tr>
    <th>#</th>
    <th>name</th>
    <th>price</th>
    <th>QTY</th>
  </tr>
</thead>
<tbody>
  {product.basket
    .map((item, index) => (
      <tr key={index}>
        <td > {index + 1} </td>
        <td>{item.x}</td>
        <td>{item.y}</td>
        <td >
          <div>
          <p> QTY: {number}</p>
    <button 
      onClick={()=>{  dispatch(plusNumber())}}
      >+</button>
   <button 
      onClick={()=>{ number? dispatch(minusNumber()):dispatch(deleteNumber(index))}}
      >-</button>
          </div>
        </td>
      </tr>
    ))}
</tbody>
</Table>
