export const basketAction = (basket) => (dispatch, getState) => {
  const x= getState().eachproduct.data.name
  const y= getState().eachproduct.data.price
  const z= getState().basket.num;
  const help={x,y,z}
  console.log(help)
  dispatch({ type: "basket", payload: [...basket,help] });
};
export const plusNumber = () => (dispatch, getState) => {
  const last = getState().number;
  dispatch({ type: "plus", payload: 1 + last });
};
export const minusNumber = () => (dispatch, getState) => {
  const last = getState().number;
  dispatch({ type: "minus", payload: last-1 });
};
export const deleteNumber = (index) => (dispatch, getState) => {
  const help= getState().basket.basket
  console.log(help)
  help.splice(index,1)
  dispatch({ type: "delete", payload: [...help] });
};
